CREATE TABLE IF NOT EXISTS `Contact` (`ContactID` int(11) NOT NULL AUTO_INCREMENT,`Name` varchar(75) NOT NULL,`Email` varchar(150) NOT NULL,`Subject` varchar(75) NOT NULL,`Message` longtext NOT NULL,`DealtWith` int(1) NOT NULL DEFAULT '0',`IPAddress` varchar(15) NOT NULL,`TimeStamp` int(11) NOT NULL,PRIMARY KEY (`ContactID`)) ENGINE=MyISAM DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `Messages` (`MessageID` int(11) NOT NULL AUTO_INCREMENT,`ThreadID` int(11) NOT NULL,`ToID` int(11) NOT NULL,`FromID` int(11) NOT NULL,`TimeStamp` int(11) NOT NULL,`Message` longtext NOT NULL,PRIMARY KEY (`MessageID`)) ENGINE=MyISAM DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `MessageThreads` (`ThreadID` int(11) NOT NULL AUTO_INCREMENT,`TimeStamp` int(11) NOT NULL,`LastMessageTime` int(11) NOT NULL,`ToID` int(11) NOT NULL,`FromID` int(11) NOT NULL,`Deleted` varchar(10) NOT NULL,`IsRead` int(1) NOT NULL DEFAULT '0',PRIMARY KEY (`ThreadID`)) ENGINE=MyISAM DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `PasswordReset` (`ResetID` int(11) NOT NULL AUTO_INCREMENT,`ResetKey` varchar(31) NOT NULL,`Email` varchar(150) NOT NULL,`TimeStamp` int(11) NOT NULL,`IPAddress` varchar(15) NOT NULL,PRIMARY KEY (`ResetID`)) ENGINE=MyISAM DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `System` (`Title` varchar(65) NOT NULL,`Copyright` varchar(75) NOT NULL,`URL` varchar(75) NOT NULL,`Email` varchar(150) NOT NULL) ENGINE=MyISAM DEFAULT CHARSET=latin1;
CREATE TABLE IF NOT EXISTS `Users` (`UserID` int(11) NOT NULL AUTO_INCREMENT,`Username` varchar(18) NOT NULL,`Password` varchar(32) NOT NULL,`Email` varchar(150) NOT NULL,`Rank` varchar(25) NOT NULL DEFAULT 'Member',`RegIP` varchar(15) NOT NULL,`LastIP` varchar(15) NOT NULL,`Sex` varchar(6) NOT NULL,`JoinDate` int(11) NOT NULL,`BirthDate` int(11) NOT NULL,`LastVisit` int(11) NOT NULL,`Picture` varchar(150) NOT NULL,`Facebook` varchar(35) NOT NULL,`Twitter` varchar(35) NOT NULL,`Website` varchar(300) NOT NULL,`AboutMe` longtext NOT NULL,`Banned` int(1) NOT NULL DEFAULT '0',`Location` varchar(25) NOT NULL,`Movies` longtext NOT NULL,`Music` longtext NOT NULL,`PageVisits` int(11) NOT NULL DEFAULT '0',`Xbox` varchar(20) NOT NULL,`PS3` varchar(25) NOT NULL,PRIMARY KEY (`UserID`)) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `WallPosts` (`WallPostID` int(11) NOT NULL AUTO_INCREMENT,`ToID` int(11) NOT NULL,`FromID` int(11) NOT NULL,`TimeStamp` int(11) NOT NULL,`IPAddress` varchar(15) NOT NULL,`Content` longtext NOT NULL,PRIMARY KEY (`WallPostID`)) ENGINE=MyISAM DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `UserAlerts` (`AlertID` int(11) NOT NULL AUTO_INCREMENT,  `ToID` int(11) NOT NULL,  `FromID` int(11) NOT NULL,  `Title` varchar(30) NOT NULL,  `Message` longtext NOT NULL,  `Displayed` int(1) NOT NULL DEFAULT '0',  `IPAddress` varchar(15) NOT NULL,  `TimeStamp` int(11) NOT NULL,  PRIMARY KEY (`AlertID`)) ENGINE=MyISAM DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;
